INSERT INTO `backgrounds` (`id`, `title`, `image`, `content`, `created_at`, `updated_at`) VALUES
(9, 'About The Forest ESV Database', NULL, '<p><span style=\"color: #ffffff; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; text-align: justify; background-color: #243413;\">The Forest ESV Database is an open-access living document of the Bangladesh Forest Department to support decision-making regarding sustainable forest governance, which consists of information on forest ecosystem services quantities and monetary values gathered from peer-reviewed journal articles, government publications, and various published project reports. This module has been developed with technical and financial support from USAID and US Forest Service&rsquo;s <a href=\"https://usfsbd.org/\">Community Partnerships to Strengthen Sustainable Development&nbsp;</a></span><a href=\"https://usfsbd.org/\">(Compass)</a><span style=\"color: #ffffff; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; text-align: justify; background-color: #243413;\"><a href=\"https://usfsbd.org/\"> Program</a>.</span></p>', '2023-08-29 05:30:44', '2023-08-30 21:47:36'),
(11, 'Database Use', NULL, '<section id=\"forth_section\">\r\n<div>\r\n<div>\r\n<div>\r\n<div>\r\n<p><span style=\"color: #ecf0f1;\">The Forest ESV Database is designed as a living document where new valuation information will be added through a rigorous review process. Voluntary contributions are welcome to enrich it. The summary statistics on the dashboard will change depending on new updates. The database should be used for non-commercial, research, and educational purposes to support sustainable forest governance and policymaking. Reports using information from the Forest ESV Database should acknowledge with a correct reference: Bangladesh Forest Department (2022). Forest Ecosystem Services Valuation Database technically implemented by US Forest Service</span></p>\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</section>', '2023-08-29 05:33:34', '2023-08-29 05:33:34'),
